import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

class Expenses extends Component {
  render() {
    const { expenses } = this.props;
    return (
      <tbody>
        { expenses.map((expense, index) => {
          const twoDecimalDigitsValue = parseFloat(expense.value).toFixed(2);
          const currencyName = Object.keys(expense.exchangeRates).filter((currency) => currency === expense.currency);
          return (
            <tr key={ index }>
              <td>{expense.description}</td>
              <td>{expense.tag}</td>
              <td>{expense.method}</td>
              <td>{expense.currency}</td>
              <td>{twoDecimalDigitsValue}</td>
              <td>defaul</td>
              <td>{expense.valueBRL}</td>
              <td>BRL</td>
              <td>
                <button type="button">Editar</button>
                <button type="button">Excluir</button>
              </td>
            </tr>
          );
        }) }
      </tbody>);
  }
}

const mapStateToProps = (state) => ({
  expenses: state.wallet.expenses,
});

Expenses.propTypes = {
  expenses: PropTypes.arrayOf(PropTypes.object).isRequired,
};

export default connect(mapStateToProps)(Expenses);
