import React from 'react';
import { Redirect } from 'react-router-dom';
import { connect } from 'react-redux';

class Login extends React.Component {
  constructor() {
    super();

    this.state = {
      emailInput: '',
      passwordInput: '',
      willRedirect: false,
    };
  }

  handleChange = ({ target }) => {
    const { name, value } = target;
    this.setState({
      [name]: value,
    });
  }

  redirectToWallet = () => {
    this.setState({
      willRedirect: true,
    });
  }

  render() {
    const { emailInput, passwordInput, willRedirect } = this.state;
    const emailRegex = /^[a-z0-9.]+@[a-z0-9]+\.[a-z]+(\.[a-z]+)?$/i;
    const minCharacterPassword = 6;
    const disabledButton = true;
    console.log(this.props);

    return (
      willRedirect
        ? <Redirect to="/carteira" />
        : (
          <div>
            <input
              type="text"
              name="emailInput"
              placeholder="E-mail"
              value={ emailInput }
              onChange={ this.handleChange }
              data-testid="email-input"
            />

            <input
              type="password"
              name="passwordInput"
              value={ passwordInput }
              onChange={ this.handleChange }
              placeholder="Senha"
              data-testid="password-input"
            />

            <button
              type="button"
              disabled={
                passwordInput.length >= minCharacterPassword
                && emailRegex.test(emailInput)
                  ? !disabledButton
                  : disabledButton
              }
              onClick={ this.redirectToWallet }
            >
              Entrar
            </button>
          </div>
        )
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  putEmailInStorage,
});

export default connect(null, mapDispatchToProps)(Login);
