import React from 'react';

class Login extends React.Component {
  constructor() {
    super();

    this.state = {
      emailInput: '',
      passwordInput: '',
    };
  }

  handleChange = ({ target }) => {
    const { name, value } = target;
    this.setState({
      [name]: value,
    });
  }

  render() {
    const { emailInput, passwordInput } = this.state;
    const emailRegex = /^[a-z0-9.]+@[a-z0-9]+\.[a-z]+(\.[a-z]+)?$/i;
    const minCharacterPassword = 6;
    const disabledButton = true;
    console.log(emailRegex.test(emailInput));

    return (
      <div>
        <input
          type="text"
          name="emailInput"
          placeholder="E-mail"
          value={ emailInput }
          onChange={ this.handleChange }
          data-testid="email-input"
        />

        <input
          type="password"
          name="passwordInput"
          value={ passwordInput }
          onChange={ this.handleChange }
          placeholder="Senha"
          data-testid="password-input"
        />

        <button
          type="button"
          disabled={
            passwordInput.length >= minCharacterPassword && emailRegex.test(emailInput)
              ? !disabledButton
              : disabledButton
          }
        >
          Entrar
        </button>
      </div>
    );
  }
}

export default Login;
